import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Books from './components/Books';  // Ensure the correct path to the Books component
import Login from './components/Login';  // Ensure the correct path to the Login component
import AddBook from './components/AddBook';  // Ensure the correct path to the AddBook component
import EditBook from './components/EditBook';  // Ensure the correct path to the EditBook component

function App() {
  const [token, setToken] = useState(null);

  const handleLogin = () => {
    // Simulating login and setting a fake token for admin
    setToken('fake-jwt-token'); 
  };

  const handleLogout = () => {
    setToken(null); // Log out by clearing the token
  };

  return (
    <Router>
      <div>
        {/* Login Button */}
        {!token ? (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        ) : (
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        )}

        {/* Navigation */}
        <nav>
          <Link to="/">Books</Link> | 
          <Link to="/add-book">Add Book</Link>
        </nav>

        {/* Define Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/add-book" element={<AddBook token={token} />} />
          <Route path="/edit-book/:id" element={<EditBook token={token} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
