import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Books from './Books';
import AddBook from './AddBook';
import EditBook from './EditBook';
import Login from './Login';

const App = () => {
  const [token, setToken] = useState('');

  // Handle login
  const handleLogin = (token) => {
    setToken(token);
  };

  return (
    <Router>
      <div>
        <h1>Book System</h1>
        {!token ? (
          <Login onLogin={handleLogin} />
        ) : (
          <Routes>
            <Route
              path="/books"
              element={<Books token={token} />}
            />
            <Route
              path="/add-book"
              element={<AddBook token={token} />}
            />
            <Route
              path="/edit-book/:id"
              element={<EditBook token={token} />}
            />
            {/* Redirect to books list after login */}
            <Route
              path="/"
              element={<Books token={token} />}
            />
          </Routes>
        )}
      </div>
    </Router>
  );
};

export default App;
