import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [books, setBooks] = useState([]);

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await fetch('http://localhost:7000/books');
        if (response.ok) {
          const data = await response.json();
          setBooks(data);
        } else {
          console.error('Failed to fetch books');
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    }
    fetchBooks();
  }, []);

  const handleLogin = () => {
    setShowModal(true);
  };

  const handleLogout = () => {
    setToken(null);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <Router>
      <div className="App" style={{ padding: '20px', minHeight: '100vh', position: 'relative' }}>
        {/* Login/Logout Button */}
        <div style={{ position: 'absolute', top: '20px', right: '20px', zIndex: 10 }}>
          {!token ? (
            <button onClick={handleLogin}>Login</button>
          ) : (
            <button onClick={handleLogout}>Logout</button>
          )}
        </div>

        {/* Navigation Links */}
        <nav style={{ marginBottom: '20px' }}>
          <Link to="/">Books</Link> | 
          {token && <Link to="/add-book">Add Book</Link>}
        </nav>

        {/* Main Content */}
        <div style={{ overflowY: 'auto', paddingBottom: '60px' }}>
          <Routes>
            <Route path="/" element={<Books books={books} />} />
            {token && <Route path="/add-book" element={<AddBook />} />}
          </Routes>
        </div>

        {/* Login Modal */}
        {showModal && <Login setToken={setToken} closeModal={closeModal} />}
      </div>
    </Router>
  );
}

export default App;
