import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleLogin = () => {
    setShowModal(true); // Show the login modal
  };

  const handleLogout = () => {
    setToken(null); // Clear the token on logout
  };

  const closeModal = () => {
    setShowModal(false); // Close the login modal
  };

  return (
    <Router>
      <div className="App">
        {/* Login/Logout Button */}
        {!token ? (
          <button 
            onClick={handleLogin} 
            style={{ 
              position: 'fixed', 
              top: '20px', 
              right: '20px', 
              zIndex: 10,
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              padding: '10px 15px',
              fontSize: '16px',
              cursor: 'pointer',
              borderRadius: '5px'
            }}
          >
            Login
          </button>
        ) : (
          <button 
            onClick={handleLogout} 
            style={{ 
              position: 'fixed', 
              top: '20px', 
              right: '20px', 
              zIndex: 10,
              backgroundColor: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '10px 15px',
              fontSize: '16px',
              cursor: 'pointer',
              borderRadius: '5px'
            }}
          >
            Logout
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> | 
          {token && <Link to="/add-book">Add Book</Link>}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          {token && <Route path="/add-book" element={<AddBook token={token} />} />}
        </Routes>

        {/* Show the Login Modal if not logged in */}
        {showModal && (
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 100
          }}>
            <div style={{
              backgroundColor: 'white',
              padding: '20px',
              borderRadius: '5px',
              width: '300px',
              textAlign: 'center'
            }}>
              <h3>Login</h3>
              <input 
                type="text" 
                placeholder="Username" 
                style={{
                  margin: '10px 0',
                  padding: '10px',
                  width: '100%',
                  border: '1px solid #ccc',
                  borderRadius: '5px'
                }} 
              />
              <input 
                type="password" 
                placeholder="Password" 
                style={{
                  margin: '10px 0',
                  padding: '10px',
                  width: '100%',
                  border: '1px solid #ccc',
                  borderRadius: '5px'
                }} 
              />
              <div>
                <button
                  onClick={() => { 
                    // Handle login logic here, then set the token
                    setToken('dummy_token');
                    closeModal();
                  }}
                  style={{
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none',
                    padding: '10px 15px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    borderRadius: '5px',
                    width: '100%'
                  }}
                >
                  Login
                </button>
                <button 
                  onClick={closeModal} 
                  style={{
                    backgroundColor: '#dc3545',
                    color: 'white',
                    border: 'none',
                    padding: '10px 15px',
                    fontSize: '16px',
                    cursor: 'pointer',
                    borderRadius: '5px',
                    marginTop: '10px',
                    width: '100%'
                  }}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
