import React, { useState, useEffect } from 'react';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!token) {
      setError("Please log in to view books.");
      setLoading(false);
      return; // Prevent fetch if no token is available
    }

    const fetchBooks = async () => {
      setLoading(true);
      setError(null);  // Reset error before fetching
      try {
        const response = await fetch('http://localhost:7000/books', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch books. Status: ' + response.status);
        }

        const data = await response.json();

        if (Array.isArray(data)) {
          setBooks(data);
        } else {
          throw new Error('Invalid data format');
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, [token]); // Dependency array ensures this runs when token changes

  // If loading, show loading message
  if (loading) {
    return <div>Loading books...</div>;
  }

  // If error occurred
  if (error) {
    return <div>Error: {error}</div>;
  }

  // If no books found
  if (books.length === 0) {
    return <div>No books found</div>;
  }

  // Render books if fetched successfully
  return (
    <div>
      <h2>Books</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <img src={book.image} alt={book.title} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
