import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const BookForm = ({ token }) => {
  const [book, setBook] = useState({
    title: '',
    author: '',
    description: '',
    publicationDate: '',
    coverImage: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      setIsEditing(true);
      const fetchBook = async () => {
        try {
          const response = await axios.get(`http://localhost:7000/books/${id}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setBook(response.data);
        } catch (err) {
          console.error(err);
        }
      };
      fetchBook();
    }
  }, [id, token]);

  const handleChange = (e) => {
    setBook({ ...book, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isEditing) {
        await axios.put(`http://localhost:7000/books/${id}`, book, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await axios.post('http://localhost:7000/books', book, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }
      navigate('/books');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>{isEditing ? 'Edit Book' : 'Add New Book'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={book.title}
          onChange={handleChange}
        />
        <input
          type="text"
          name="author"
          placeholder="Author"
          value={book.author}
          onChange={handleChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={book.description}
          onChange={handleChange}
        />
        <input
          type="date"
          name="publicationDate"
          value={book.publicationDate}
          onChange={handleChange}
        />
        <input
          type="text"
          name="coverImage"
          placeholder="Cover Image URL"
          value={book.coverImage}
          onChange={handleChange}
        />
        <button type="submit">{isEditing ? 'Update Book' : 'Add Book'}</button>
      </form>
    </div>
  );
};

export default BookForm;
