import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(null); // Token state
  const [showModal, setShowModal] = useState(false); // For the login modal
  const [books, setBooks] = useState([]); // State to hold books data

  // Fetch books when the app starts (no login required)
  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await fetch('http://localhost:7000/books');
        if (response.ok) {
          const data = await response.json();
          setBooks(data);
        } else {
          console.error('Failed to fetch books');
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    }
    fetchBooks();
  }, []);

  // Function to handle login modal
  const handleLogin = () => {
    setShowModal(true); // Show the login modal
  };

  const handleLogout = () => {
    setToken(null); // Clear the token on logout
  };

  const closeModal = () => {
    setShowModal(false); // Close the login modal
  };

  return (
    <Router>
      <div className="App">
        {/* Login/Logout Button */}
        {!token ? (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        ) : (
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> | 
          {token && <Link to="/add-book">Add Book</Link>}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books books={books} />} />
          {token && <Route path="/add-book" element={<AddBook />} />}
        </Routes>

        {/* Show the Login Modal if not logged in */}
        {showModal && <Login setToken={setToken} closeModal={closeModal} />}
      </div>
    </Router>
  );
}

export default App;
