import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Login from './components/Login'; // Make sure the path is correct
import BookList from './components/BookList'; // Assuming this exists for displaying books
import AddBook from './components/AddBook'; // Assuming this exists
import EditBook from './components/EditBook'; // Assuming this exists
import RemoveBook from './components/RemoveBook'; // Assuming this exists

const App = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [books, setBooks] = useState([]);

  // Fetch books on component mount
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/books'); // Replace with your API
        if (response.ok) {
          const data = await response.json();
          setBooks(data);  // Assuming API returns an array of books
        } else {
          console.error('Error fetching books');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };
    fetchBooks();
  }, []);

  const openModal = () => setModalOpen(true);
  const closeModal = () => setModalOpen(false);

  return (
    <Router>
      <div>
        <nav>
          <Link to="/">Home</Link>
          {!token && <button onClick={openModal}>Login</button>}
          {token && <button onClick={() => setToken(null)}>Logout</button>}
        </nav>

        {modalOpen && <Login closeModal={closeModal} setToken={setToken} />}

        <Routes>
          <Route path="/" element={<BookList books={books} />} />
          <Route path="/add" element={<AddBook />} />
          <Route path="/edit/:id" element={<EditBook />} />
          <Route path="/remove/:id" element={<RemoveBook />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
