import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate(); // For programmatically navigating after login/logout

  // Sync token with localStorage when it changes
  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  // Login button logic
  const handleLogin = () => {
    setShowModal(true);
  };

  // Logout button logic
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
    navigate('/'); // Navigate to home after logout
  };

  // Close the modal
  const closeModal = () => {
    setShowModal(false);
  };

  // Handle successful login
  const handleLoginSuccess = (newToken) => {
    setToken(newToken);
    closeModal();
    navigate('/'); // Navigate to home after login
  };

  return (
    <div className="App">
      {/* Login/Logout Button */}
      {token ? (
        <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
          Logout
        </button>
      ) : (
        <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
          Login
        </button>
      )}

      {/* Navigation Links */}
      <nav>
        <Link to="/">Books</Link> |{' '}
        {token && (
          <>
            <Link to="/add-book">Add Book</Link> |{' '}
            <Link to="/remove-book">Remove Book</Link> |{' '}
            <Link to="/edit-book">Edit Book</Link>
          </>
        )}
      </nav>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Books token={token} />} />
        {token && <Route path="/add-book" element={<AddBook token={token} />} />}
      </Routes>

      {/* Show the Login Modal if not logged in */}
      {showModal && <Login setToken={handleLoginSuccess} closeModal={closeModal} />}
    </div>
  );
}

export default App;
