import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';  // Import useNavigate for routing

import Login from './Login';  // Assuming you have a Login component
import AddBook from './AddBook';  // Assuming you have an AddBook component
import RemoveBook from './RemoveBook';  // Assuming you have a RemoveBook component
import EditBook from './EditBook';  // Assuming you have an EditBook component

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('token'));  // Use token to check logged-in status
  const navigate = useNavigate();  // Get the navigate function

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/');  // Navigate to the home page (or login page) after logging out
  };

  return (
    <div>
      <nav>
        <ul>
          <li><a href="/">Home</a></li>
          {token ? (
            <>
              <li><a href="/add-book">Add Book</a></li>
              <li><a href="/remove-book">Remove Book</a></li>
              <li><a href="/edit-book">Edit Book</a></li>
              <li><button onClick={handleLogout}>Logout</button></li>
            </>
          ) : (
            <li><a href="/login">Login</a></li>
          )}
        </ul>
      </nav>

      <Routes>
        <Route path="/" element={<h1>Welcome to the Bookstore</h1>} />
        <Route path="/login" element={<Login setToken={setToken} />} />
        <Route path="/add-book" element={<AddBook />} />
        <Route path="/remove-book" element={<RemoveBook />} />
        <Route path="/edit-book" element={<EditBook />} />
      </Routes>
    </div>
  );
};

export default App;
