import React, { useEffect, useState } from 'react';

function Books() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await fetch('http://localhost:7000/books');
        if (response.ok) {
          const data = await response.json();
          console.log('Books fetched:', data); // Log the data to see if the first 22 books are missing
          setBooks(data);
        } else {
          console.error('Failed to fetch books');
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    }
    fetchBooks();
  }, []);

  return (
    <div>
      <h2>Books</h2>
      {books.length > 0 ? (
        <ul style={{ paddingLeft: '0', listStyleType: 'none' }}>
          {books.map((book) => (
            <li key={book.id} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <img src={book.image} alt={book.title} style={{ width: '100px', height: '150px' }} />
            </li>
          ))}
        </ul>
      ) : (
        <p>No books found.</p>
      )}
    </div>
  );
}

export default Books;
