import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(null); // Store the JWT token
  const [showModal, setShowModal] = useState(false); // Manage the modal visibility

  // Handle login button click to show the login modal
  const handleLogin = () => {
    setShowModal(true); // Show the login modal
  };

  // Handle logout, clearing the token
  const handleLogout = () => {
    setToken(null); // Clear the token
    localStorage.removeItem('token'); // Optionally remove token from localStorage
  };

  // Close the login modal
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <Router>
      <div className="App">
        {/* Login/Logout Button */}
        {!token ? (
          <button
            onClick={handleLogin}
            style={{ position: 'absolute', top: '20px', right: '20px' }}
          >
            Login
          </button>
        ) : (
          <button
            onClick={handleLogout}
            style={{ position: 'absolute', top: '20px', right: '20px' }}
          >
            Logout
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> |
          {token && <Link to="/add-book">Add Book</Link>}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          {token && <Route path="/add-book" element={<AddBook token={token} />} />}
        </Routes>

        {/* Show the Login Modal if not logged in */}
        {showModal && <Login setToken={setToken} closeModal={closeModal} />}
      </div>
    </Router>
  );
}

export default App;
