import React, { useState, useEffect } from 'react';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch books only if the token is available
  useEffect(() => {
    if (!token) return; // Don't fetch if token is not present

    const fetchBooks = async () => {
      setLoading(true);  // Set loading true when starting fetch
      setError(null);    // Reset error before starting new fetch
      try {
        const response = await fetch('http://localhost:7000/books', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,  // Include token in request
          },
        });

        // Check response status
        if (!response.ok) {
          throw new Error(`Failed to fetch books. Status: ${response.status}`);
        }

        const data = await response.json();
        
        // Ensure we have valid data to set
        if (Array.isArray(data)) {
          setBooks(data); // Update state with the fetched books
        } else {
          throw new Error('Invalid data format');
        }
      } catch (err) {
        setError(err.message);  // Capture and display any error
      } finally {
        setLoading(false);  // Set loading to false once the fetch is done
      }
    };

    fetchBooks(); // Call fetchBooks only when the token changes
  }, [token]);  // Dependency on token ensures we fetch again when token changes

  // If loading is true, show loading state
  if (loading) {
    return <div>Loading books...</div>;
  }

  // If there was an error, show error message
  if (error) {
    return <div>Error: {error}</div>;
  }

  // If no books found
  if (books.length === 0) {
    return <div>No books found</div>;
  }

  // Render the books if the fetch was successful
  return (
    <div>
      <h2>Books</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <img src={book.image} alt={book.title} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
