import React, { useState } from 'react';

function Login({ setToken, closeModal }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    const response = await fetch('http://localhost:7000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    if (response.ok) {
      const data = await response.json();
      setToken(data.token);
      closeModal(); // Close the login modal on successful login
    } else {
      alert('Login failed');
    }
  };

  return (
    <div style={{
      position: 'fixed', top: '0', left: '0', width: '100%', height: '100%',
      backgroundColor: 'rgba(0, 0, 0, 0.5)', display: 'flex', justifyContent: 'center',
      alignItems: 'center', zIndex: '1000'
    }}>
      <div style={{
        background: 'white', padding: '20px', borderRadius: '5px', width: '300px',
        textAlign: 'center'
      }}>
        <h2>Login</h2>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          style={{ width: '100%', padding: '10px', marginBottom: '20px' }}
        />
        <button onClick={handleLogin} style={{ width: '100%', padding: '10px' }}>Login</button>
      </div>
    </div>
  );
}

export default Login;
