import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import Books from './Books';
import AddBook from './AddBook';
import EditBook from './EditBook';
import Login from './Login';

const App = () => {
  const [token, setToken] = useState(null);

  // Example login function
  const handleLogin = (userToken) => {
    setToken(userToken);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            {token ? (
              <>
                <li>
                  <Link to="/books">Books</Link>
                </li>
                <li>
                  <Link to="/add-book">Add Book</Link>
                </li>
              </>
            ) : (
              <li>
                <Link to="/login">Login</Link>
              </li>
            )}
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<h2>Welcome to the Book Store</h2>} />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/books" element={token ? <Books token={token} /> : <Login onLogin={handleLogin} />} />
          <Route path="/add-book" element={token ? <AddBook token={token} /> : <Login onLogin={handleLogin} />} />
          <Route path="/edit-book/:id" element={token ? <EditBook token={token} /> : <Login onLogin={handleLogin} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
