import React, { useState } from 'react';

const Login = ({ setToken, closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send login request to backend
    const response = await fetch('http://localhost:7000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();

    if (response.status === 401) {
      setErrorMessage(data.message); // Show error message if login fails
    } else {
      setToken(data.token); // Store token in parent component
      closeModal(); // Close the login modal
    }
  };

  return (
    <div style={modalStyle} onClick={closeModal}>
      <div style={modalContentStyle} onClick={(e) => e.stopPropagation()}>
        <button onClick={closeModal} style={closeButtonStyle}>X</button>
        <h2 style={headingStyle}>Login</h2>
        <form onSubmit={handleSubmit} style={formStyle}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={inputStyle}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={inputStyle}
          />
          <button type="submit" style={submitButtonStyle}>Login</button>
        </form>
        {errorMessage && <p style={errorMessageStyle}>{errorMessage}</p>}
      </div>
    </div>
  );
};

// Inline CSS styles
const modalStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  zIndex: 1000,
};

const modalContentStyle = {
  backgroundColor: '#fff',
  padding: '20px',
  borderRadius: '8px',
  width: '300px',
  position: 'relative',
};

const closeButtonStyle = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  fontSize: '20px',
  background: 'transparent',
  border: 'none',
  cursor: 'pointer',
};

const headingStyle = {
  textAlign: 'center',
  marginBottom: '20px',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const inputStyle = {
  padding: '10px',
  marginBottom: '10px',
  width: '100%',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const submitButtonStyle = {
  padding: '10px 20px',
  backgroundColor: '#007BFF',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

const errorMessageStyle = {
  color: 'red',
  textAlign: 'center',
};

export default Login;
