import React, { useState } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import Books from './components/Books';  // Make sure the path is correct
import Login from './components/Login';  // Make sure the path is correct
import AddBook from './components/AddBook';  // Make sure the path is correct
import EditBook from './components/EditBook';  // Make sure the path is correct

function App() {
  const [token, setToken] = useState(null);

  const handleLogin = () => {
    // Simulate login and set a token for admin access
    setToken('fake-jwt-token');
  };

  const handleLogout = () => {
    // Simulate logout by clearing the token
    setToken(null);
  };

  return (
    <div>
      {/* Login/Logout Button */}
      {!token ? (
        <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
          Login
        </button>
      ) : (
        <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
          Logout
        </button>
      )}

      {/* Navigation Links */}
      <nav>
        <Link to="/">Books</Link> | 
        {token && <Link to="/add-book">Add Book</Link>}
      </nav>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Books token={token} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/add-book" element={<AddBook token={token} />} />
        <Route path="/edit-book/:id" element={<EditBook token={token} />} />
      </Routes>
    </div>
  );
}

export default App;
