import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import RemoveBook from './components/RemoveBook';
import EditBook from './components/EditBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Re-check token on page load to reflect logged-in state
    if (token) {
      localStorage.setItem('token', token); // Save token to localStorage
    } else {
      localStorage.removeItem('token'); // Remove token if logged out
    }
  }, [token]);

  const handleLogin = () => {
    setShowModal(true);
  };

  const handleLogout = () => {
    setToken(null);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleLoginSuccess = (token) => {
    setToken(token);
    closeModal();
  };

  return (
    <Router>
      <div className="App">
        {/* Login/Logout Button */}
        {token ? (
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        ) : (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> | 
          {token && (
            <>
              <Link to="/add-book">Add Book</Link> | 
              <Link to="/remove-book">Remove Book</Link> | 
              <Link to="/edit-book">Edit Book</Link>
            </>
          )}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          {token && <Route path="/add-book" element={<AddBook token={token} />} />}
          {token && <Route path="/remove-book" element={<RemoveBook token={token} />} />}
          {token && <Route path="/edit-book" element={<EditBook token={token} />} />}
        </Routes>

        {/* Show the Login Modal if not logged in */}
        {showModal && <Login setToken={handleLoginSuccess} closeModal={closeModal} />}
      </div>
    </Router>
  );
}

export default App;
