// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Books from './components/Books';
import Login from './components/Login';
import AddBook from './components/AddBook';  // Add this if you want to have a page to add books
import EditBook from './components/EditBook'; // Similarly, edit books

function App() {
  const [token, setToken] = useState(null);

  const handleLogin = () => {
    // Simulating login by setting a token (you can replace it with actual JWT logic)
    setToken('fake-jwt-token'); // This would be replaced with the actual login functionality
  };

  return (
    <Router>
      <div>
        {/* Login Button */}
        {!token ? (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        ) : (
          <button onClick={() => setToken(null)} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        )}
        
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/add-book" element={<AddBook />} />
          <Route path="/edit-book/:id" element={<EditBook />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
