import React, { useState, useEffect } from 'react';

function Login({ setToken, closeModal }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Send login request to the backend
    fetch('http://localhost:7000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error('Invalid login credentials');
      })
      .then((data) => {
        setToken(data.token); // Set token on successful login
        closeModal(); // Close the modal after successful login
      })
      .catch((error) => {
        alert(error.message); // Show an error message if login fails
      });
  };

  useEffect(() => {
    const handleOutsideClick = (e) => {
      if (e.target.id === 'login-modal') {
        closeModal(); // Close the modal if clicked outside
      }
    };

    document.addEventListener('mousedown', handleOutsideClick);

    // Cleanup the event listener when component unmounts
    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, [closeModal]);

  return (
    <div id="login-modal" style={modalStyle}>
      <div style={modalContentStyle}>
        <button
          onClick={closeModal}
          style={closeButtonStyle}
        >
          X
        </button>
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label>Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

// Modal styles
const modalStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const modalContentStyle = {
  backgroundColor: 'white',
  padding: '20px',
  borderRadius: '5px',
  position: 'relative',
  width: '300px',
};

const closeButtonStyle = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  border: 'none',
  background: 'none',
  fontSize: '20px',
  cursor: 'pointer',
};

export default Login;
