import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch books with JWT authentication
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:7000/books', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(response.data);
        setLoading(false);
      } catch (err) {
        setError('Error fetching books');
        setLoading(false);
        console.error(err);
      }
    };

    fetchBooks();
  }, [token]);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:7000/books/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setBooks(books.filter((book) => book.id !== id));
    } catch (err) {
      console.error(err);
      setError('Error deleting book');
    }
  };

  // Show loading, error, or books
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h2>Books List</h2>
      <Link to="/add-book">Add New Book</Link>
      <ul>
        {books.length > 0 ? (
          books.map((book) => (
            <li key={book.id}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>{book.description}</p>
              <img src={book.coverImage} alt={book.title} />
              <Link to={`/edit-book/${book.id}`}>Edit</Link>
              <button onClick={() => handleDelete(book.id)}>Delete</button>
            </li>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </ul>
    </div>
  );
};

export default Books;
