import React, { useEffect, useState } from 'react';

function Books({ token }) {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Only fetch books if the token exists
        if (token) {
          const response = await fetch('http://localhost:7000/books', {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const data = await response.json();
            setBooks(data);
          } else {
            console.log('Error fetching books:', response.statusText);
            setBooks([]);
          }
        }
      } catch (error) {
        console.error('Error fetching books:', error);
        setBooks([]);
      }
    };

    fetchBooks();
  }, [token]);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Books</h1>
      {books.length === 0 ? (
        <p>Loading books...</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {books.map((book) => (
            <div 
              key={book.id} 
              style={{
                border: '1px solid #ccc',
                borderRadius: '5px',
                width: '200px',
                padding: '10px',
                boxSizing: 'border-box',
                textAlign: 'center',
                backgroundColor: '#f9f9f9'
              }}
            >
              {/* Display book image */}
              <img
                src={book.image || 'https://via.placeholder.com/150'}
                alt={book.title}
                style={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: '5px',
                  objectFit: 'cover',
                }}
              />
              <h3 style={{ fontSize: '16px', fontWeight: 'bold', marginTop: '10px' }}>{book.title}</h3>
              <p style={{ fontSize: '14px', color: '#555' }}>{book.author}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Books;
