import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:7000/books', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBooks(response.data);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError('Failed to load books');
        setLoading(false);
      }
    };

    if (token) {
      fetchBooks();
    } else {
      setLoading(false);
    }
  }, [token]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Books List</h1>
      <ul>
        {books.length > 0 ? (
          books.map((book) => (
            <li key={book.id}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>{book.description}</p>
              {/* Check if the cover image exists */}
              {book.coverImage && (
                <img
                  src={`http://localhost:7000/${book.coverImage}`} // Make sure the image URL is correct
                  alt={book.title}
                  style={{ width: '100px', height: 'auto' }} // Style the image size
                />
              )}
            </li>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </ul>
    </div>
  );
};

export default Books;
