import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import Login from './Login';
import AddBook from './AddBook';
import RemoveBook from './RemoveBook';
import EditBook from './EditBook';

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [showAdminOptions, setShowAdminOptions] = useState(false);
  const navigate = useNavigate(); // Use navigate hook for programmatic navigation

  // Check if token exists and if it's valid (you can add more checks if needed)
  useEffect(() => {
    if (token) {
      // You could add more validation here (e.g., decoding the token to check if it's valid)
      setShowAdminOptions(true);  // This can be updated to check if the user is an admin based on the token
    } else {
      setShowAdminOptions(false);
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token from localStorage
    setToken(null); // Reset token state
    navigate('/'); // Redirect to homepage after logout
  };

  return (
    <Router>
      <div className="App">
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            {token ? (
              <>
                <li><Link to="/add-book">Add Book</Link></li>
                <li><Link to="/remove-book">Remove Book</Link></li>
                <li><Link to="/edit-book">Edit Book</Link></li>
                <li><button onClick={handleLogout}>Logout</button></li>
              </>
            ) : (
              <li><Link to="/login">Login</Link></li>
            )}
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<h1>Welcome to the Book System</h1>} />
          <Route path="/login" element={<Login setToken={setToken} />} />
          <Route path="/add-book" element={showAdminOptions ? <AddBook /> : <h1>Access Denied</h1>} />
          <Route path="/remove-book" element={showAdminOptions ? <RemoveBook /> : <h1>Access Denied</h1>} />
          <Route path="/edit-book" element={showAdminOptions ? <EditBook /> : <h1>Access Denied</h1>} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
