import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Only fetch books if there is a token (logged in)
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:7000/books', {
          headers: { Authorization: `Bearer ${token}` }, // Ensure the token is passed
        });
        console.log(response.data); // Add a console log to verify the response
        setBooks(response.data); // Set books in state
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        console.error(err);
        setError('Failed to load books');
        setLoading(false);
      }
    };

    if (token) {
      fetchBooks(); // Only fetch if token exists (logged in)
    } else {
      setLoading(false); // Set loading false if no token
    }
  }, [token]);

  if (loading) {
    return <div>Loading...</div>; // Display loading text while data is being fetched
  }

  if (error) {
    return <div>{error}</div>; // Display error message if any
  }

  return (
    <div>
      <h1>Books List</h1>
      <ul>
        {books.length > 0 ? (
          books.map((book) => (
            <li key={book.id}>
              <h3>{book.title}</h3>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Description:</strong> {book.description}</p>
            </li>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </ul>
    </div>
  );
};

export default Books;
