import React, { useEffect, useState } from 'react';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:7000/books');
        const data = await response.json();
        setBooks(data);
      } catch (error) {
        console.error("Error fetching books:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleDelete = async (bookId) => {
    try {
      const response = await fetch(`http://localhost:7000/books/${bookId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete book');
      }

      // Remove the deleted book from the state
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
      alert('Book deleted successfully!');
    } catch (error) {
      console.error("Error deleting book:", error);
      alert('Failed to delete book');
    }
  };

  if (loading) {
    return <p>Loading books...</p>;
  }

  return (
    <div>
      <h1>Books</h1>
      {books.length === 0 ? (
        <p>No books available.</p>
      ) : (
        <ul>
          {books.map((book) => (
            <li key={book.id}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>{book.description}</p>
              <img src={book.coverImage} alt={book.title} style={{ width: '150px' }} />
              
              {token && (
                <>
                  <button onClick={() => alert(`Editing book with ID: ${book.id}`)}>
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(book.id)}
                    style={{ backgroundColor: 'red', color: 'white', marginLeft: '10px' }}
                  >
                    Delete
                  </button>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Books;
