import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Books from './components/Books';  // Ensure path is correct
import Login from './components/Login';  // Ensure path is correct
import AddBook from './components/AddBook';  // Ensure path is correct
import EditBook from './components/EditBook';  // Ensure path is correct

function App() {
  const [token, setToken] = useState(null);

  // Handle login
  const handleLogin = () => {
    setToken('fake-jwt-token');  // Simulate login
  };

  // Handle logout
  const handleLogout = () => {
    setToken(null);  // Simulate logout
  };

  return (
    <Router>
      <div>
        {/* Login/Logout Button */}
        {!token ? (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        ) : (
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> | 
          {token && <Link to="/add-book">Add Book</Link>} {/* Only show 'Add Book' link if logged in */}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} /> {/* Books page */}
          <Route path="/login" element={<Login />} />
          <Route path="/add-book" element={token ? <AddBook token={token} /> : <Login />} /> {/* If not logged in, show login */}
          <Route path="/edit-book/:id" element={token ? <EditBook token={token} /> : <Login />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
