import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom'; // Remove BrowserRouter from here
import Login from './components/Login';
import AddBook from './components/AddBook';
import RemoveBook from './components/RemoveBook';
import EditBook from './components/EditBook';

const App = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [books, setBooks] = useState([]);

  // Fetch books on component mount
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:7000/api/books');
        if (response.ok) {
          const data = await response.json();
          setBooks(data);
        } else {
          console.error('Error fetching books');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };
    fetchBooks();
  }, []);

  const openModal = () => setModalOpen(true);
  const closeModal = () => setModalOpen(false);

  return (
    <div>
      <nav>
        <Link to="/">Home</Link>
        {!token && <button onClick={openModal}>Login</button>}
        {token && <button onClick={() => setToken(null)}>Logout</button>}
      </nav>

      {modalOpen && <Login closeModal={closeModal} setToken={setToken} />}

      <Routes>
        <Route
          path="/"
          element={
            <div>
              <h2>Books List</h2>
              <ul>
                {books.map((book) => (
                  <li key={book.id}>
                    {book.title} - {book.author}
                  </li>
                ))}
              </ul>
            </div>
          }
        />
        <Route path="/add" element={<AddBook />} />
        <Route path="/edit/:id" element={<EditBook />} />
        <Route path="/remove/:id" element={<RemoveBook />} />
      </Routes>
    </div>
  );
};

export default App;
