import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import Login from './components/Login';

function App() {
  const [token, setToken] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleLogin = () => {
    setShowModal(true); // Show the login modal
  };

  const handleLogout = () => {
    setToken(null); // Clear the token on logout
  };

  const closeModal = () => {
    setShowModal(false); // Close the login modal
  };

  return (
    // This should be the only place where Router is used
    <Router>
      <div>
        {/* Login/Logout Button */}
        {!token ? (
          <button onClick={handleLogin} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Login
          </button>
        ) : (
          <button onClick={handleLogout} style={{ position: 'absolute', top: '20px', right: '20px' }}>
            Logout
          </button>
        )}

        {/* Navigation Links */}
        <nav>
          <Link to="/">Books</Link> | 
          {token && <Link to="/add-book">Add Book</Link>}
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Books token={token} />} />
          {token && <Route path="/add-book" element={<AddBook token={token} />} />}
        </Routes>

        {/* Show the Login Modal if not logged in */}
        {showModal && <Login setToken={setToken} closeModal={closeModal} />}
      </div>
    </Router>
  );
}

export default App;
