import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Replace this URL with the actual backend URL where the books.json is served
    axios
      .get('http://localhost:5000/data/books.json') // Adjust this URL as per your backend's endpoint
      .then((response) => {
        // Check if the data exists and is in the expected format
        if (response && response.data) {
          setBooks(response.data);
        } else {
          setError('No data available');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError('Error fetching books');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Book List</h1>
      <ul>
        {books.length > 0 ? (
          books.map((book) => (
            <li key={book.id}>
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <img src={book.coverImage} alt={book.title} />
            </li>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </ul>
    </div>
  );
};

export default BookList;
