import React, { useState } from 'react';

const Login = ({ setToken, closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Replace this URL with your actual backend login endpoint
    const response = await fetch('http://localhost:7000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();

    if (response.ok) {
      setToken(data.token); // Store the token on successful login
      closeModal(); // Close the login modal
    } else {
      alert('Login failed: ' + data.message); // Show error message
    }
  };

  return (
    <div className="login-modal">
      <div className="modal-content">
        <button onClick={closeModal} style={{ float: 'right' }}>X</button>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
