import React, { useState, useEffect } from 'react';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import AddBook from './components/AddBook';
import RemoveBook from './components/RemoveBook';
import EditBook from './components/EditBook';

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [showAdminOptions, setShowAdminOptions] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (token) {
      setShowAdminOptions(true);
    } else {
      setShowAdminOptions(false);
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/');
  };

  return (
    <div className="App">
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          {token ? (
            <>
              <li><Link to="/add-book">Add Book</Link></li>
              <li><Link to="/remove-book">Remove Book</Link></li>
              <li><Link to="/edit-book">Edit Book</Link></li>
              <li><button onClick={handleLogout}>Logout</button></li>
            </>
          ) : (
            <li><Link to="/login">Login</Link></li>
          )}
        </ul>
      </nav>

      <Routes>
        <Route path="/" element={<h1>Welcome to the Book System</h1>} />
        <Route path="/login" element={<Login setToken={setToken} />} />
        <Route path="/add-book" element={showAdminOptions ? <AddBook /> : <h1>Access Denied</h1>} />
        <Route path="/remove-book" element={showAdminOptions ? <RemoveBook /> : <h1>Access Denied</h1>} />
        <Route path="/edit-book" element={showAdminOptions ? <EditBook /> : <h1>Access Denied</h1>} />
      </Routes>
    </div>
  );
};

export default App;
