import React, { useState, useEffect } from 'react';

const Books = ({ token }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!token) return;  // Don't fetch if there's no token

    const fetchBooks = async () => {
      setLoading(true);  // Start loading when fetching starts

      try {
        const response = await fetch('http://localhost:7000/books', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,  // Send the token for authentication
          },
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch books. Status: ${response.status}`);
        }

        const data = await response.json();
        setBooks(data);  // Set books data from backend
      } catch (err) {
        setError(err.message);  // Set error message if fetch fails
      } finally {
        setLoading(false);  // Stop loading once the fetch is complete
      }
    };

    fetchBooks();  // Fetch books whenever the token changes
  }, [token]);  // Re-run the effect when the token changes

  // Show loading spinner or message
  if (loading) {
    return <div>Loading books...</div>;
  }

  // Show error message if any
  if (error) {
    return <div>Error: {error}</div>;
  }

  // Show no books message if the array is empty
  if (!books.length) {
    return <div>No books found</div>;
  }

  // Display the books if they are successfully fetched
  return (
    <div>
      <h2>Books</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <img src={book.image} alt={book.title} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
