import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Books from './components/Books';
import AddBook from './components/AddBook';
import EditBook from './components/EditBook';
import Login from './components/Login';

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('token') || null);

  useEffect(() => {
    // You can add logic here to validate the token or perform checks if needed.
  }, [token]);

  const handleLogin = (token) => {
    setToken(token);
    localStorage.setItem('token', token); // Save token to localStorage
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token'); // Remove token from localStorage
  };

  return (
    <Router>
      <div>
        {/* Login Button */}
        {token ? (
          <button onClick={handleLogout} style={{ position: 'absolute', top: 20, right: 20 }}>
            Logout
          </button>
        ) : (
          <button onClick={() => navigate('/login')} style={{ position: 'absolute', top: 20, right: 20 }}>
            Admin Login
          </button>
        )}

        <Routes>
          {/* Books Route - Always available to users */}
          <Route path="/books" element={<Books token={token} />} />

          {/* Login Route */}
          <Route
            path="/login"
            element={token ? <Navigate to="/books" /> : <Login onLogin={handleLogin} />}
          />

          {/* Add Book Route */}
          <Route
            path="/add-book"
            element={!token ? <Navigate to="/login" /> : <AddBook token={token} />}
          />

          {/* Edit Book Route */}
          <Route
            path="/edit-book/:id"
            element={!token ? <Navigate to="/login" /> : <EditBook token={token} />}
          />

          {/* Default Route (if none match) */}
          <Route path="*" element={<Navigate to="/books" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
